package typecast;

public class Typecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          byte b=45;
          int i=67;
          double result=b+i;
             System.out.println(result); //TypeCasting

           double d=88.9;
           int c=(int)d;
             System.out.println(c); //TypeConversion
             
           String name="Richa";
           String Usn="1AY23AI092";
           int Age=20;
           float Height=5.2f;
        		   System.out.println(name);
                   System.out.println(Usn);
                   System.out.println(Age);
                   System.out.println(Height); //Variable
        }

}
