package Array;

public class row3_column4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int ar[][]= {{1,2,3,4},{4,5,6,7},{8,9,3,5}};
	    for(int i=0;i<3;i++)
	    {
	    	for(int j=0;j<4;j++)
	    	{
	    		System.out.print(ar[i][j]);
	    	}
	       System.out.println();
	}

}
}
